import { takeLatest, call, put , all} from 'redux-saga/effects';
import UserActionTypes from "./user.type";
import{ googleprovider , createDocument , signInWithGoogle , auth}from "../../firebase/firebase.utility";
import{ googleSignInSuccess , googleSignInFailure , emailSignInStart , emailSignInFailure} from "./user.actions."

export function* signInWithGooglepop() {
    try {
const { user } = yield auth.signInWithPopup(googleprovider)
const userRef = yield call( createDocument , user);
const snapShot = yield userRef.get();
yield put(googleSignInSuccess({
   id : snapShot.id,
    ...snapShot.data()
}))
    } catch (error) {
        yield put(googleSignInFailure(error))
    }
}


export function* onGooglesignin() {
    yield takeLatest(
        UserActionTypes.GOOGLE_SIGN_IN_START,
        signInWithGooglepop
    )
}
export function* onEmailsignin() {
    yield takeLatest(
        UserActionTypes.EM_SIGN_IN_START,
        signInWithGooglepop
    )
}

export function* userSagas() {
    yield all([call(onGooglesignin)])
}
