import CartActionTypes  from './cart.type';

const INITIAL_STATE = {
  hidden: true,
  cart
};

const cartReducer = (state = INITIAL_STATE, {type}) => {
  switch (type) {
    case CartActionTypes.TOGGLE_CART_HIDDEN:
      return {
        ...state,
        hidden: !state.hidden
      };
    default:
      return state;
  }
};

export default cartReducer
