import React , { Component} from 'react';
import { Route } from 'react-router-dom';
import Shopextent from '../../components/shopext/shopextent';
import CollectionPage from '../collection/collectionpreview';
import { ShopPageContainer } from './shoppage.styled';
import{ firestore , convertColleectionsSnapshotTomap } from "../../firebase/firebase.utility"


class Shoppage extends Component {

 componentDidMount(){
    const collectionRef = firestore.collection("collection")
     firestore.onSnapshot( async snapshot => convertColleectionsSnapshotTomap(snapshot))
 }

   render(){
      const { match } = this.props
   return  (
      <ShopPageContainer>
      <Route exact path={`${match.path}`} component={Shopextent}/>
      <Route path={`${match.path}/:collectionId`} component={CollectionPage} />
         </ShopPageContainer>
       )
   }
}

 export default Shoppage;


